// Private methods //
/////////////////////

// fire the stage one timeout
TwoStageTimer.prototype._fireS1 = function () {  
    this._s1p();
    this._s2TimeoutID = setTimeout(this._fireS2, this._s2d);
};

// fire the stage two timeout
TwoStageTimer.prototype._fireS2 = function () {
    this._s2p();
    this._started = false;
};

