    function fixPath(path) {
        if (path) {
            if (path.indexOf('blob:') !== -1) {
                return path;
            } else if (path.indexOf('http') !== -1) {
                path = path.replace(/http.?.?\/\/[^\/]*/, '');
            }
            if (path.indexOf('/') !== 0) {
                path = '/' + path;
            }
            return _db.getFileURL() + path;
        }
    }

