            playHolder.css({
                top: playSize.y + 2 + 'px',
                width: playSize.width + 'px',
                height: playSize.height + 'px',
            });

            // scale seek slider
            var seekSize = LADS.Util.constrainAndPosition(
                controlPanel.width(), controlPanel.height(),
                {
                    width: 0.7,
                    height: 1,
                    max_width: 620,
                    max_height: 20,
                    center_v: true,
                }
            );

