    function sortTimeline(artworks, tag) {
        var identical = 0;
        var comparator;
        var valuation;
        var avlTree;
        var artNode;
        var i;
        if (tag === 'Title') {
            identical = 0;
            comparator = function (a, b) {
                if (a.nameKey < b.nameKey) {
                    return -1;
                } else if (a.nameKey > b.nameKey) {
                    return 1;
                } else {
                    if (a.artwork.Identifier > b.artwork.Identifier) {
                        return 1;
                    }
                    else if (a.artwork.Identifier < b.artwork.Identifier) {
                        return -1;
                    } else {
                        return 0;
                    }
                }
            };

            valuation = function (value, compareToNode) {
                if (!compareToNode) {
                    return null;
                } else if (value < compareToNode.nameKey) {
                    return -1;
                } else if (value > compareToNode.nameKey) {
                    return 1;
                } else {
                    return 0;
                }
            };

            avlTree = new AVLTree(comparator, valuation);
            avlTree.clear();
            for (i = 0; i < artworks.length; i++) {
                artNode = {
                    artwork: artworks[i],
                    nameKey: artworks[i].Name,
                };
                avlTree.add(artNode);
            }
            return avlTree;
        } else if (tag === 'Artist') {
            identical = 0;
            comparator = function (a, b) {
                if (a.artistKey < b.artistKey) {
                    return -1;
                } else if (a.artistKey > b.artistKey) {
                    return 1;
                } else {
                    if (a.artwork.Identifier > b.artwork.Identifier) {
                        return 1;
                    } 
                    else if (a.artwork.Identifier < b.artwork.Identifier) {
                        return -1;
                    } else {
                        return 0;
                    }
                }
            };

            valuation = function (value, compareToNode) {
                if (!compareToNode) {
                    return null;
                } else if (value < compareToNode.artistKey) {
                    return -1;
                } else if (value > compareToNode.artistKey) {
                    return 1;
                } else {
                    return 0;
                }
            };

            avlTree = new AVLTree(comparator, valuation);
            for (i = 0; i < artworks.length; i++) {
                artNode = {
                    artwork: artworks[i],
                    artistKey: artworks[i].Type === 'Empty' ? '~~~~' : artworks[i].Metadata.Artist
                };
                avlTree.add(artNode);
            }
            return avlTree;
        } else if (tag === 'Year') {
            identical = 0;
            comparator = function (a, b) {
                if (a.yearKey < b.yearKey) {
                    return -1;
                } else if (a.yearKey > b.yearKey) {
                    return 1;
                } else {
                    if (a.artwork.Identifier > b.artwork.Identifier) {
                        return 1;
                    }
                    else if (a.artwork.Identifier < b.artwork.Identifier) {
                        return -1;
                    } else {
                        return 0;
                    }
                }
            };

            valuation = function (value, compareToNode) {
                if (!compareToNode) {
                    return null;
                } else if (value < compareToNode.yearKey) {
                    return -1;
                } else if (value > compareToNode.yearKey) {
                    return 1;
                } else {
                    return 0;
                }
            };

            avlTree = new AVLTree(comparator, valuation);
            for (i = 0; i < artworks.length; i++) {
                artNode = {
                    artwork: artworks[i],
                    yearKey: artworks[i].Type === 'Empty' ? '~~~~' : artworks[i].Metadata.Year
                };
                avlTree.add(artNode);
            }
            return avlTree;
        } else if (tag === 'Type') {
            identical = 0;
            comparator = function (a, b) {
                if (a.typeKey < b.typeKey) {
                    return -1;
                } else if (a.typeKey > b.typeKey) {
                    return 1;
                } else {
                    if (a.nameKey > b.nameKey) {
                        return 1;
                    }
                    else if (a.nameKey < b.nameKey) {
                        return -1;
                    } else {
                        return 0;
                    }
                }
            };

            valuation = function (value, compareToNode) {
                if (!compareToNode) {
                    return null;
                } else if (value < compareToNode.nameKey) {
                    return -1;
                } else if (value > compareToNode.nameKey) {
                    return 1;
                } else {
                    return 0;
                }
            };

            avlTree = new AVLTree(comparator, valuation);
            for (i = 0; i < artworks.length; i++) {
                artNode = {
                    artwork: artworks[i],
                    nameKey: artworks[i].Name,
                    typeKey: artworks[i].Type === 'Empty' ? 1 : (artworks[i].Metadata.Type === 'Artwork' ? 2 : 3)
                };
                avlTree.add(artNode);
            }
            return avlTree;
        }
        else return null; // error case: should check 'tag'
    }

